import './App.css';
import React, { useState } from 'react';
import PrintArticle from './Article';



const tasks = [
  { title: 'Apprendre React', completed: true ,status:1},
  { title: 'Créer un projet React', completed: false ,status:1},
  { title: 'Comprendre les props', completed: true ,status:199},
  { title: 'Déployer une application React', completed: false,status:1 }
];

function Terminer(props){
  props.status = 0;
}

function Task(props) {
  return (
    <div style={{ margin: '10px', padding: '10px', border: '1px solid #ccc' }}>
      <p>Titre : {props.title}</p>
      <p>Status : {props.completed ? 'Terminée' : 'En cours'}</p>
      <button onClick={Terminer}>Terminé</button>
    </div>
  );
}

function TaskList(props) {
  return (
    <div>
      {props.tasks.map((task, index) =>
        task.status === 1 ? (
          <Task key={index} title={task.title} completed={task.completed} />
        ) : null
      )}
    </div>
  );
}

function AddList() {
  // État pour la liste des tâches
  const [tasks, setTasks] = useState([
    { title: 'Apprendre React', completed: true, status: 1 },
    { title: 'Créer un projet React', completed: false, status: 1 },
    { title: 'Comprendre les props', completed: true, status: 199 },
    { title: 'Déployer une application React', completed: false, status: 1 }
  ]);

  // État pour les champs d'entrée
  const [newTaskTitle, setNewTaskTitle] = useState('');
  const [newTaskCompleted, setNewTaskCompleted] = useState(false);

  // Fonction pour ajouter une nouvelle tâche
  const addNewTask = () => {
    if (newTaskTitle.trim() === '') {
      alert('Le titre de la tâche ne peut pas être vide.');
      return;
    }

    const newTask = {
      title: newTaskTitle,
      completed: newTaskCompleted,
      status: 1
    };

    setTasks([...tasks, newTask]); // Ajoute la nouvelle tâche au tableau
    setNewTaskTitle(''); // Réinitialise le champ titre
    setNewTaskCompleted(false); // Réinitialise le statut
  };

  return (
    <div>
      <TaskList tasks={tasks} />

      <h3>Ajouter une nouvelle tâche</h3>
      <input
        type="text"
        value={newTaskTitle}
        onChange={(e) => setNewTaskTitle(e.target.value)}
        placeholder="Titre de la tâche"
      />
      <label>
        <input
          type="checkbox"
          checked={newTaskCompleted}
          onChange={(e) => setNewTaskCompleted(e.target.checked)}
        />
        Terminée
      </label>
      <button onClick={addNewTask}>Ajouter</button>
    </div>
  );
}











function App() {


  return (
    <div className="App">
      <h1>Liste des tâches</h1>
      <AddList />
    </div>
  );
}

export default App;
