import './App.css';
import React, { useState } from 'react';
import PrintArticle from './Article';



const tasks = [
  { title: 'Apprendre React', completed: true },
  { title: 'Créer un projet React', completed: false },
  { title: 'Comprendre les props', completed: true },
  { title: 'Déployer une application React', completed: false }
];



function Task(props) {
  return (
    <div style={{ margin: '10px', padding: '10px', border: '1px solid #ccc' }}>
      <p>Titre : {props.title}</p>
      <p>Status : {props.completed ? 'Terminée' : 'En cours'}</p>
    </div>
  );
}

function TaskList(props) {
  return (
    <div>
      {props.tasks.map((task, index) => (
        <Task key={index} title={task.title} completed={task.completed} />
      ))}
    </div>
  );
}

function AddList(){

  const [inputValue1,setInputValue] = useState(''); // Pour stocker la valeur de l'input
  const [inputValue2,setInputValue2] = useState('');
  const [value, setValue] = useState(''); // Pour stocker la valeur après avoir appuyé sur "Ajouter"
  const [value2, setValue2] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value); // Met à jour l'état de l'input à chaque changement
  };

  const handleAddClick = () => {
    setValue(inputValue1); // Lorsque l'on clique sur "Ajouter", on stocke la valeur de l'input dans `value`
    setValue2(inputValue2);
    setInputValue(''); // Réinitialise le champ d'entrée après l'ajout
  };

  return (
    <div>
      <input 
        type="text" 
        value={inputValue} 
        onChange={handleInputChange} 
        placeholder="Entrez le titre"
      />
       <input 
        type="text" 
        value={inputValue} 
        onChange={handleInputChange} 
        placeholder="Entrez l'avancement"
      />


      <button onClick={handleAddClick}>Ajouter</button>

      <p>La valeur stockée est : {value} {}</p>
    </div>
  );
}








function App() {


  return (
    <div className="App">
      <h1>Liste des tâches</h1>
      <TaskList tasks={tasks} />
      <AddList />
    </div>
  );
}

export default App;
