import './App.css';
import React, { useState, useEffect } from 'react';


function Task(props) {
  return (
    <div style={{ margin: '10px', padding: '10px', border: '1px solid #ccc' }}>
      <p>Titre : {props.title}</p>
      <p>Status : {props.completed ? 'Terminée' : 'En cours'}</p>
      <button onClick={props.onComplete}>Terminer</button>
    </div>
  );
}

function AddList() {
    // État pour la liste des tâches
    const [tasks, setTasks] = useState([]);

    // Charger les tâches depuis localStorage au démarrage
    useEffect(() => {
      const savedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
      setTasks(savedTasks);
    }, []);

    // Sauvegarder les tâches dans localStorage à chaque mise à jour
    useEffect(() => {
      localStorage.setItem('tasks', JSON.stringify(tasks));
    }, [tasks]);

    // État pour les champs d'entrée
    const [newTaskTitle, setNewTaskTitle] = useState('');
    const [newTaskCategory, setNewTaskCategory] = useState('Travail');
    const [filter, setFilter] = useState('all');

    // Fonction pour ajouter une nouvelle tâche
    const addNewTask = () => {
      if (newTaskTitle.trim() === '') {
        alert('Le titre de la tâche ne peut pas être vide.');
        return;
      }

      const newTask = {
        title: newTaskTitle,
        completed: false,
        category: newTaskCategory,
      };

      setTasks([...tasks, newTask]); // Ajoute la nouvelle tâche au tableau
      setNewTaskTitle(''); // Réinitialise le champ titre
      setNewTaskCategory('Travail'); // Réinitialise la catégorie
    };

    // Fonction pour marquer une tâche comme terminée
    const completeTask = (index) => {
      const updatedTasks = tasks.map((task, i) =>
        i === index ? { ...task, completed: true } : task
      );
      setTasks(updatedTasks);
    };

    // Filtrer les tâches selon le statut
    const filteredTasks = tasks.filter((task) => {
      if (filter === 'completed') return task.completed;
      if (filter === 'pending') return !task.completed;
      return true; // Affiche toutes les tâches
    });

    return (
      <div className="box">
        <h1>To-Do List</h1>

        {/* Filtrage des tâches */}
        <div>
          <button onClick={() => setFilter('all')}>Toutes</button>
          <button onClick={() => setFilter('pending')}>En cours</button>
          <button onClick={() => setFilter('completed')}>Terminées</button>
        </div>

        {/* Liste des tâches */}
        <TaskList tasks={filteredTasks} onCompleteTask={completeTask} />

        {/* Formulaire pour ajouter une tâche */}
        <h3>Ajouter une nouvelle tâche</h3>
        <input
          style={{ width: '300px', height: '30px' }}
          type="text"
          value={newTaskTitle}
          onChange={(e) => setNewTaskTitle(e.target.value)}
          placeholder="Titre de la tâche"
        />
        <br />
        <select
          value={newTaskCategory}
          onChange={(e) => setNewTaskCategory(e.target.value)}
          style={{ width: '300px', height: '30px', margin: '10px 0' }}
        >
          <option value="Travail">Travail</option>
          <option value="Personnel">Personnel</option>
          <option value="Loisirs">Loisirs</option>
        </select>
        <br />
        <button style={{ width: '300px', height: '30px' }} onClick={addNewTask}>
          Ajouter
        </button>
      </div>
    );
  }



function TaskList({ tasks, onCompleteTask }) {
  return (
    <div>
      {tasks.map((task, index) => (
        <Task
          key={index}
          title={task.title}
          completed={task.completed}
          onComplete={() => onCompleteTask(index)}
        />
      ))}
    </div>
  );
}



function App() {
  return (
    <div className="App">
      <h1>To-Do-List</h1>
      <AddList />
    </div>
  );
}

export default App;
