import './App.css';
import React, { useState } from 'react';
import PrintArticle from './Article';



const tasks = [
  { title: 'Apprendre React', completed: true ,status:1},
  { title: 'Créer un projet React', completed: false ,status:1},
  { title: 'Comprendre les props', completed: true ,status:199},
  { title: 'Déployer une application React', completed: false,status:1 }
];


function Task(props) {
  const handleComplete = () => {
    props.onComplete(props.id); // Appelle une fonction depuis le parent pour mettre à jour l'état
  };

  return (
    <div style={{ margin: '10px', padding: '10px', border: '1px solid #ccc' }}>
      <p>Titre : {props.title}</p>
      <p>Status : {props.completed ? 'Terminée' : 'En cours'}</p>
      {!props.completed && <button onClick={handleComplete}>Terminé</button>}
    </div>
  );
}

function TaskList() {
  // État pour les tâches
  const [tasks, setTasks] = useState([
    { id: 1, title: 'Apprendre React', completed: false },
    { id: 2, title: 'Créer un projet React', completed: false },
    { id: 3, title: 'Comprendre les props', completed: true },
  ]);

  // Fonction pour marquer une tâche comme terminée
  const markTaskAsCompleted = (id) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === id ? { ...task, completed: true } : task
      )
    );
  };

  return (
    <div>
      <h2>Liste des tâches</h2>
      {tasks
        .filter((task) => !task.completed) // Filtrer uniquement les tâches "en cours"
        .map((task) => (
          <Task
            key={task.id}
            id={task.id}
            title={task.title}
            completed={task.completed}
            onComplete={markTaskAsCompleted}
          />
        ))}
    </div>
  );
}






