import './App.css';
import React, { useState } from 'react';
import PrintArticle from './Article';



const tasks = [
  { title: 'Apprendre React', completed: true ,status:1},
  { title: 'Créer un projet React', completed: false ,status:1},
  { title: 'Comprendre les props', completed: true },
  { title: 'Déployer une application React', completed: false,status:1 }
];



function Task(props) {
  return (
    <div style={{ margin: '10px', padding: '10px', border: '1px solid #ccc' }}>
      <p>Titre : {props.title}</p>
      <p>Status : {props.completed ? 'Terminée' : 'En cours'}</p>
    </div>
  );
}

function TaskList(props) {
  return (
    <div>
      {props.tasks.map((task, index) => (

        <Task key={index} title={task.title} completed={task.completed} />
      ))}
    </div>
  );
}

function AddList(){
  const [inputValue1, setInputValue1] = useState(''); // Pour stocker la valeur du premier input
  const [inputValue2, setInputValue2] = useState(''); // Pour stocker la valeur du deuxième input
  const [value, setValue] = useState(''); // Pour stocker la valeur du premier input après avoir appuyé sur "Ajouter"
  const [value2, setValue2] = useState(''); // Pour stocker la valeur du deuxième input après avoir appuyé sur "Ajouter"

  // Met à jour la valeur du premier champ d'entrée
  const handleInputChange1 = (event) => {
    setInputValue1(event.target.value); 
  };

  // Met à jour la valeur du deuxième champ d'entrée
  const handleInputChange2 = (event) => {
    setInputValue2(event.target.value); 
  };

  // Lorsque l'on clique sur "Ajouter", on stocke les valeurs des deux inputs dans `value` et `value2`
  const handleAddClick = () => {
    setValue(inputValue1);
    setValue2(inputValue2);
    setInputValue1(''); // Réinitialise le premier champ d'entrée après l'ajout
    setInputValue2(''); // Réinitialise le deuxième champ d'entrée après l'ajout
  };

  return (
    <div>
      <input 
        type="text" 
        value={inputValue1} 
        onChange={handleInputChange1} 
        placeholder="Entrez le titre"
      />
      <input 
        type="text" 
        value={inputValue2} 
        onChange={handleInputChange2} 
        placeholder="Entrez l'avancement"
      />

      <button onClick={handleAddClick}>Ajouter</button>

      <p>La valeur du titre est : {value}</p>
      <p>La valeur de l'avancement est : {value2}</p>
    </div>
  );
}








function App() {


  return (
    <div className="App">
      <h1>Liste des tâches</h1>
      <TaskList tasks={tasks} />
      <AddList />
    </div>
  );
}

export default App;
